import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect} from 'react';
import { fetchUsers } from './actions/action';
import SearchForm from './Components/SearchForm';
import UserList from './Components/UsersList';
import UserDetails from './Components/UserDetails';



const App = () => {
  const { users, error } = useSelector(state => state);
  const [selectedUser, setSelectedUser] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const dispatch = useDispatch();

  const handleSearch = query => {
    dispatch(fetchUsers(query));
  };

  const handleUserClick = user => {
    setSelectedUser(user);
    console.log(user);
  };

  useEffect(() => {
    dispatch(fetchUsers(''));
  }, [dispatch]);

  useEffect(() => {
    if (users.length === 0 && !error) {
      setIsLoading(true);
    } else {
      setIsLoading(false);
    }
  }, [users, error]);

  return (
    <div>
      <h1 className="header-name">Search GitHub Users</h1>
      <SearchForm onSearch={handleSearch} />
      <div className="row">
        <div className="col-md-6">
          {isLoading ? (
            <div className="placeholder">
              <img src={PlaceholderImage} alt="Loading..." />
            </div>
          ) : error ? (
            <p>Error: {error}</p>
          ) : (
            <UserList users={users} onItemClick={handleUserClick} />
          )}
        </div>
        <div className="col-md-6">
          <UserDetails user={selectedUser} />
        </div>
      </div>
    </div>
  );
};

export default App;
