import React from 'react';
import '../Components/UserList.css'
import  { useState, useEffect } from 'react';

const UserList = ({ users, onItemClick, loadMore }) => {
  const [isFetching, setIsFetching] = useState(false);

  const handleScroll = () => {
    if (
      window.innerHeight + document.documentElement.scrollTop !==
        document.documentElement.offsetHeight ||
      isFetching
    ) {
      return;
    }

    setIsFetching(true);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    if (!isFetching) return;

    loadMore().then(() => setIsFetching(false));
  }, [isFetching, loadMore]);

  return (
    <ul className="user-list">
      {users.map(user => (
        <li key={user.id} className="user-list-item" onClick={() => onItemClick(user)}>
          <a href={`/users/${user.username}`} className="d-flex align-items-center">
            <img src={user.avatar_url} alt={user.login} className="user-avatar" />
            <span className="user-username">{user.login}</span>
          </a>
        </li>
      ))}
      {isFetching && <p>Loading more...</p>}
    </ul>
  );
};

export default UserList;
