import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect} from 'react';
import { fetchUsers } from './actions/action';
import SearchForm from './Components/SearchForm';
import UserList from './Components/UsersList';
import UserDetails from './Components/UserDetails';



const App = () => {
  const { loading, users, error } = useSelector(state => state);
  const [selectedUser, setSelectedUser] = useState(null);
  const [page, setPage] = useState(1); // Хранит текущую страницу
  const [isLoading, setIsLoading] = useState()

  const dispatch = useDispatch();

  const handleSearch = query => {
    dispatch(fetchUsers(query));
  };

  const handleUserClick = user => {
    setSelectedUser(user);
    // Дополнительная логика при клике на пользователя
    console.log(user);
  };

  const loadMore = () => {
    // Увеличиваем номер страницы на 1
    setPage(prevPage => prevPage + 1);
  };

  useEffect(() => {
    const handleScroll = () => {
      // Проверяем, достигли ли мы конца страницы
      if (
        window.innerHeight + document.documentElement.scrollTop >=
        document.documentElement.offsetHeight
      ) {
        loadMore();
      }
    };

    // Добавляем обработчик события scroll к окну браузера
    window.addEventListener('scroll', handleScroll);

    // Очищаем обработчик события scroll при размонтировании компонента
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Пустой массив зависимостей, чтобы эффект выполнился только один раз при монтировании компонента

  useEffect(() => {
    // Загружаем пользователей при загрузке компонента
    dispatch(fetchUsers('')).then(() => {
      setIsLoading(false);
    });
  }, [dispatch]);

  return (
    <div>
      <h1 className="header-name">Search GitHub Users</h1>
      <SearchForm onSearch={handleSearch} />
      <div className="row">
        <div className="col-md-6">
          {loading ? (
            <div className="loading-container">
              <img src={loadingImage} alt="Loading..." className="loading-image" />
            </div>
          ) : error ? (
            <p>Error: {error}</p>
          ) : (
            <UserList users={users} onItemClick={handleUserClick} />
          )}
        </div>
        <div className="col-md-6">
          <UserDetails user={selectedUser} />
        </div>
      </div>
    </div>
  );
};

export default App;
