import { useState } from "react";
import { useDispatch } from '../';
import { setUsers } from "../actions/action";

const SearchForm = () => {

  const [username, setUsername] = useState('');
  const dispatch = useDispatch();

  const handleInputChange = (event) => {
    setUsername(event.target.value);
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`https://api.github.com/search/users?q=${username}`);
      const data = await response.json();
      const users = data.items;
      dispatch(setUsers(users)); // Обновляем состояние Redux с найденными пользователями
    } catch (error) {
      console.error(error);
    }
  };

    // Здесь мы будем выполнять поиск пользователей на GitHub


  return (
    <form onSubmit={handleFormSubmit}>
      <input
        type="text"
        placeholder="Введите имя пользователя GitHub"
        value={username}
        onChange={handleInputChange}
      />
      <button type="submit">Поиск</button>
    </form>
  );
};

export default SearchForm;
