import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useState , useEffect,useRe} from 'react';
import SearchForm from './Components/SearchForm';
import UserList from './Components/UsersList';
import UserDetails from './Components/UserDetails';
import { fetchUsers } from './actions/action';


const App = () => {
  const { loading, users, error } = useSelector(state => state);
  const [selectedUser, setSelectedUser] = useState(null);
  const [visibleUsers, setVisibleUsers] = useState(10); // Количество отображаемых пользователей
  const dispatch = useDispatch();
  const userListRef = useRef(null);

  const handleSearch = query => {
    dispatch(fetchUsers(query));
  };

  const handleUserClick = user => {
    setSelectedUser(user);
  };

  const handleScroll = () => {
    const scrollPosition = userListRef.current.scrollTop;
    const elementHeight = userListRef.current.clientHeight;
    const contentHeight = userListRef.current.scrollHeight;

    if (scrollPosition + elementHeight >= contentHeight) {
      loadMore();
    }
  };

  const loadMore = () => {
    setVisibleUsers(prevCount => prevCount + 10);
  };

  useEffect(() => {
    dispatch(fetchUsers('')); // Fetch users initially
  }, [dispatch]);

  useEffect(() => {
    userListRef.current.addEventListener('scroll', handleScroll);
    return () => {
      userListRef.current.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div>
      <h1 className="header-name">Search GitHub Users</h1>
      <SearchForm onSearch={handleSearch} />
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <>
          <UserList ref={userListRef} users={users.slice(0, visibleUsers)} onItemClick={handleUserClick} />
        </>
      )}
      <UserDetails user={selectedUser} />
    </div>
  );
};

export default App;
