// reducers.js
import { combineReducers } from 'redux';

const initialState = {
  users: [],
  selectedUser: null,
};

const usersReducer = (state = initialState.users, action) => {
  switch (action.type) { 
    case 'SET_USERS':
      return action.payload;
    default:
      return state;
  }
};

const selectedUserReducer = (state = initialState.selectedUser, action) => {
  switch (action.type) {
    case 'SET_SELECTED_USER':
      return action.payload;
    default:
      return state;
  }
};

const rootReducer = combineReducers({
  users: usersReducer,
  selectedUser: selectedUserReducer,
});

export default rootReducer;
