import React from 'react';

const UserDetails = ({ user }) => {
  const [followersCount, setFollowersCount] = useState(0);
  const [followingCount, setFollowingCount] = useState(0);

  useEffect(() => {
    if (user) {
      // Запросить количество подписчиков
      axios
        .get(user.followers_url)
        .then(response => {
          setFollowersCount(response.data.length);
        })
        .catch(error => {
          console.error('Error fetching followers:', error);
        });

      // Запросить количество подписок
      axios
        .get(user.following_url.replace('{/other_user}', ''))
        .then(response => {
          setFollowingCount(response.data.length);
        })
        .catch(error => {
          console.error('Error fetching following:', error);
        });
    }
  }, [user]);

  if (!user) {
    return <div>No user selected.</div>;
  }

  const {
    login,
    avatar_url,
    followers_url,
    following_url,
    company,
    email,
    blog
  } = user;

  return (
    <div>
      <img src={avatar_url} alt={login} />
      <h1>{login}</h1>
      <ul>
      <li>Followers: {followersCount}</li>
        <li>Following: {followingCount}</li>
        <li>Company: {company ?? 'No company'}</li>
        <li>Email: {email ?? 'No email'}</li>
        <li>Blog: {blog ?? 'No blog'}</li>
      </ul>
    </div>
  );
};

export default UserDetails;
