import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';
import { fetchUsers } from './actions/action';
import SearchForm from './Components/SearchForm';
import UserList from './Components/UsersList';
import UserDetails from './Components/UserDetails';



const App = () => {
  const { loading, users, error } = useSelector(state => state);
  const [selectedUser, setSelectedUser] = useState(null);
  const [page, setPage] = useState(1); // Хранит текущую страницу
  const [perPage, setPerPage] = useState(10); // Хранит количество пользователей на странице

  const dispatch = useDispatch();

  const handleSearch = query => {
    dispatch(fetchUsers(query));
  };

  const handleUserClick = user => {
    setSelectedUser(user);
    // Дополнительная логика при клике на пользователя
    console.log(user);
  };

  const loadMore = () => {
    // Увеличиваем номер страницы на 1
    setPage(prevPage => prevPage + 1);
  };

  const changePerPage = newPerPage => {
    setPerPage(newPerPage);
    setPage(1); // Сбрасываем номер страницы при изменении количества пользователей на странице
  };

  const startIndex = (page - 1) * perPage;
  const endIndex = startIndex + perPage;
  const displayedUsers = users.slice(startIndex, endIndex); // Отображаем только нужное количество пользователей

  return (
    <div>
      <h1 className="header-name">Search GitHub Users</h1>
      <SearchForm onSearch={handleSearch} />
      <div className="row">
        <div className="col-md-6">
          {loading ? (
            <p>Loading...</p>
          ) : error ? (
            <p>Error: {error}</p>
          ) : (
            <UserList users={displayedUsers} onItemClick={handleUserClick} onLoadMore={loadMore} />
          )}
          <div>
            <label htmlFor="perPage">Users per Page:</label>
            <select
              id="perPage"
              value={perPage}
              onChange={e => changePerPage(Number(e.target.value))}
            >
              <option value="5">5</option>
              <option value="10">10</option>
              <option value="20">20</option>
            </select>
          </div>
        </div>
        <div className="col-md-6">
          <UserDetails user={selectedUser} />
        </div>
      </div>
    </div>
  );
};

export default App;
