import React from 'react';
import { useSelector } from 'react-redux';

const UserDetails = () => {
  const selectedUser = useSelector((state) => state.selectedUser);

  if (!selectedUser) {
    return <div>Пользователь не выбран</div>;
  }

  return (
    <div>
      <img src={selectedUser.avatar_url} alt={selectedUser.login} />
      <h2>{selectedUser.name}</h2>
      <p>Последователи: {selectedUser.followers}</p>
      <p>Следующий: {selectedUser.following}</p>
      <p>Компания: {selectedUser.company}</p>
      <p>Электронная почта: {selectedUser.email}</p>
      <p>Блог: {selectedUser.blog}</p>
    </div>
  );
};

export default UserDetails;
